let output = "";
let icons = {
	"ac": 0x26E8,
	"hp": 0x1F5A4,
	"speed": 0x1F97E,
	"Perception": 0x1F440,
	"Investigation": 0x1F50E,
	"Insight": 0x1F4A1,
}
let statWithIcon = (targetStat, substat) => {
	if (targetStat == undefined || targetStat in monster === false) {
		return "";
	}

	let stat = monster[targetStat];
	let icon = icons[targetStat];
	if ( substat !== undefined && substat in stat ){
		stat = stat[substat];
		icon = icons[substat];
	}
	return `${String.fromCodePoint(icon)}${stat} `;
}
output += statWithIcon("ac");
output += statWithIcon("hp");
output += statWithIcon("speed");
output += statWithIcon("passives", "Perception");
output += statWithIcon("passives", "Investigation");
output += statWithIcon("passives", "Insight");
return output.trim();

let getMod = (statVal) => {
  let mod = Math.floor(((statVal ?? 10) - 10) / 2);
  return `${mod > 0 ? "+" : mod < 0 ? "-" : ""}${Math.abs(mod)}`;
}

let output = "";
let labels = ["Str","Dex","Con","Int","Wis","Cha"];
if ("stats" in monster) {
  let stats = monster.stats;
  for ( let x = 0; x < stats.length; x++){
    let stat = stats[x];
    let label = labels[x];
    let mod = getMod(stat);
    output += `${label}: ${stat} (${mod}) `;
  }
  return output.trim();
}
return "";
